import React, {Component}  from 'react';
import {withRouter, BrowserRouter as Router, Switch,Route,HashRouter } from 'react-router-dom';
import {TransitionGroup,CSSTransition} from 'react-transition-group';
import './animation.css';
import ScrollToTop from './components/ScrollToTop.js';
import Logo from './components/logo.js';
import Home from './Home.js';
import {AboutPage} from './About.js';
import {ProductPage} from './Product.js';
import {NewsPage} from './News.js';
import {ContactPage} from './Contact.js';
import HeaderMenu from './components/header.js';


const AppRoute =({location}) => {
  const currentkey = location.pathname.split("/")[1] || ""

    return (
      <div>
  <HashRouter>
    <TransitionGroup>
    <CSSTransition key={currentkey} className='fade' timeout={800}>
      <ScrollToTop>
        <div>
          <Switch location={location}>
          <Route exact path='/' component={Home}/>
        <Route path='/About' component={AboutPage}/>
        <Route path='/Product' component={ProductPage}/>
        <Route path='/News' component={NewsPage}/>
        <Route path='/Contact' component={ContactPage}/>
        </Switch>
            </div>
            <HeaderMenu/>
          </ScrollToTop>

    </CSSTransition>
  </TransitionGroup>
    </HashRouter>
      </div>
    );
}






class App extends Component {
    render(){
      return(
        <div>
          <AppRoute/>
          <Logo/>

        </div>
      )
    }

  }


export default App;
